// SPDX-License-Identifier: MIT
pragma solidity ^0.7.6;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./WhiteToken.sol";
import "./IStakingContract.sol";

contract StakingContract is Ownable {
    using SafeMath for uint256;

    WhiteToken public immutable whiteTokenContract;
    IERC20 public whiteTokenInterface;
    struct Stake {
        uint256 amount;
        uint256 earnings;
    }

    struct TokenInfo {
        bool isEnabled;
        uint256 totalDeposited;
    }

    mapping(address => Stake) public stakers;
    mapping(address => TokenInfo) public tokens;
    mapping(address => mapping(address => uint256)) public erc20Earnings;
    address[] public enabledTokens;
    address[] public stakerAddresses;
    uint256 public totalStaked;
    uint256 public totalEarnings;

    constructor(address whiteTokenAddress) {
        whiteTokenContract = WhiteToken(whiteTokenAddress);
        whiteTokenInterface = IERC20(whiteTokenAddress);
    }

    function stake(uint256 amount) external {
        uint256 amountInWei = amount.mul(10**uint256(whiteTokenContract.decimals()));
        uint256 commission = amountInWei.mul(5).div(100);
        uint256 amountAfterCommission = amountInWei.sub(commission);

        require(whiteTokenInterface.transferFrom(msg.sender, address(this), amountInWei), "Transfer failed");
        require(whiteTokenInterface.transfer(owner(), commission), "Commission transfer failed");

        Stake storage staker = stakers[msg.sender];
        staker.amount = staker.amount.add(amountAfterCommission);
        totalStaked = totalStaked.add(amountAfterCommission);
        if (stakers[msg.sender].amount == amountAfterCommission) {
            stakerAddresses.push(msg.sender);
        }
}
    function unstake() external {
    Stake storage staker = stakers[msg.sender];
    require(staker.amount > 0, "No staking balance");

    _withdrawEarnings(msg.sender);
    require(whiteTokenInterface.transfer(msg.sender, staker.amount), "WhiteToken transfer failed");
    totalStaked = totalStaked.sub(staker.amount);
    staker.amount = 0;
        if (stakers[msg.sender].amount == 0) {
            _removeStakerAddress(msg.sender);
        }
    }

    function harvest() external {
        _withdrawEarnings(msg.sender);
    }

function _withdrawEarnings(address user) private {
    Stake storage staker = stakers[user];
    
    uint256 bnbEarnings = staker.earnings;
    if (bnbEarnings > 0) {
        staker.earnings = 0;
        (bool success, ) = payable(user).call{value: bnbEarnings}("");
        require(success, "BNB Transfer failed");
    }

    for (uint256 i = 0; i < enabledTokens.length; i++) {
        address tokenAddress = enabledTokens[i];
        uint256 tokenEarnings = erc20Earnings[user][tokenAddress];
        if (tokenEarnings > 0) {
            erc20Earnings[user][tokenAddress] = 0;
            IERC20(tokenAddress).transfer(user, tokenEarnings);
        }
    }
}

    function _removeStakerAddress(address stakerAddress) private {
        uint256 length = stakerAddresses.length;
        for (uint256 i = 0; i < length; i++) {
            if (stakerAddresses[i] == stakerAddress) {
                stakerAddresses[i] = stakerAddresses[length - 1];
                stakerAddresses.pop();
                break;
            }
        }
    }

    function enableToken(address tokenAddress) external onlyOwner {
        tokens[tokenAddress].isEnabled = true;
    }

    function disableToken(address tokenAddress) external onlyOwner {
        tokens[tokenAddress].isEnabled = false;
    }

function deposit() external payable {
    require(msg.value > 0, "No BNB sent");
    require(totalStaked > 0, "No WhiteTokens staked");

    for (uint i = 0; i < stakerAddresses.length; i++) {
        address stakerAddress = stakerAddresses[i];
        Stake storage staker = stakers[stakerAddress];
        if (staker.amount > 0) {
            uint256 share = msg.value.mul(staker.amount).div(totalStaked);
            staker.earnings = staker.earnings.add(share);
        }
    }

    totalEarnings = totalEarnings.add(msg.value);
}

function depositERC20(address tokenAddress, uint256 depositAmount) external {
    require(tokens[tokenAddress].isEnabled, "Token not enabled");
    require(depositAmount > 0, "No ERC20 tokens sent");

    IERC20 token = IERC20(tokenAddress);
    require(token.transferFrom(msg.sender, address(this), depositAmount), "Transfer failed");
    if (totalStaked > 0) {
        for (uint i = 0; i < stakerAddresses.length; i++) {
            address stakerAddress = stakerAddresses[i];
            Stake storage staker = stakers[stakerAddress];
            if (staker.amount > 0) {
                uint256 share = depositAmount.mul(staker.amount).div(totalStaked);
                erc20Earnings[stakerAddress][tokenAddress] = erc20Earnings[stakerAddress][tokenAddress].add(share);
            }
        }
    }

    tokens[tokenAddress].totalDeposited = tokens[tokenAddress].totalDeposited.add(depositAmount);
}

    function getTotalDeposited(address tokenAddress) external view returns (uint256) {
        return tokens[tokenAddress].totalDeposited;
    }

function getUserEarnings(address user, address tokenAddress) external view returns (uint256 bnbEarningsAmount, uint256 erc20EarningsAmount) {
    Stake storage staker = stakers[user];
    bnbEarningsAmount = staker.earnings;
    erc20EarningsAmount = erc20Earnings[user][tokenAddress];
}

function getTotalEarnings() external view returns (uint256 totalBnbEarnings, uint256[] memory totalErc20Earnings, address[] memory erc20Tokens) {
    totalBnbEarnings = totalEarnings;
    erc20Tokens = enabledTokens;

    totalErc20Earnings = new uint256[](enabledTokens.length);
    for (uint256 i = 0; i < enabledTokens.length; i++) {
        address tokenAddress = enabledTokens[i];
        uint256 totalTokenEarnings = tokens[tokenAddress].totalDeposited;
        totalErc20Earnings[i] = totalTokenEarnings;
    }
}

}
